// --- Split Name Function for n8n ---
// Sample for testing purpose
const sampleBuyerName = "Fachridan Tio Mu'afa"
const buyerName = $('Webhook Response').first().json.body.buyer_name
const fullName = buyerName.trim() || '';
const parts = fullName.split(/\s+/).filter(Boolean);

let firstName = '';
let lastName = '';

if (parts.length === 1) {
  // Case 0: Handles single names like "Fachridan"
  firstName = parts[0];
  lastName = parts[0];
} else if (parts.length > 1) {
  // Cases 1-4: Handles multi-word names
  // Assign the last word to lastName
  lastName = parts.pop();
  // Join the remaining parts for the firstName
  firstName = parts.join(' ');
}

// Returns the final object in the n8n item structure
return {
  json: {
    firstName: firstName,
    lastName: lastName
  }
};

